function GetVersion(id,version){

GM_xmlhttpRequest ({
		method: "GET",
		url: "http://userscripts.org/scripts/show/" + id,
		headers: {
					"User-agent": "Mozilla/5.0",
					"Accept": "text/html",
				 },
		onload: function (respuesta){
			var userScripts = document.implementation.createDocument ("", "", null);
			var html = document.createElement ("html");
			html.innerHTML = respuesta.responseText;
			userScripts.appendChild (html);
			
			//Get new version
			var newVersion = userScripts.getElementById ("summary").getElementsByTagName("b")[1].nextSibling.textContent;
			
			//Get the name of the script
			var name = userScripts.getElementById ("details").childNodes[1].innerHTML;
			
			if (trim (newVersion) != trim (version)){
					var aaa = document.createElement ("div");
					aaa.setAttribute ("id", "paypal");
					aaa.setAttribute ("style","position: fixed; background-color: rgba(64, 64, 64, 0.5); "+
							"left: 0px; width: 80px; height:17px; z-index: 40000; "+
							"text-align: center; color: rgb(204, 204, 204); font-weight: bold; "+
							"	-moz-border-radius-bottomleft: 6px; cursor: pointer;");	
					aaa.innerHTML = "<a id=\"donateButton\" class='fontColorRace' style='cursor:pointer;'>NewVersion ("+newVersion+")</a>";	
					document.getElementsByTagName("body")[0].appendChild(aaa);
			}
		}
}); //mishu
}